
public class Pattern {
	public void Patt()
	{
		for(int i=0;i<=5;i++)
		{
		for(int j=0;j<=i;j++)
			{
				System.out.println(" " +i );
			}
		
		}
		
	}

	public static void main(String[] args) 
	{
		// TODO Auto-generated method stub
		Pattern p=new Pattern();
		p.Patt();
		
		}
	}


	
